        const playerId = otherResult.playerId;

        if (!playerTotals[playerId]) {
            playerTotals[playerId] = 0;
        }

        playerTotals[playerId] += otherResult.score;
    }

    // Step 2: Find the best (lowest) total score from all other players
    const bestOtherTotal = Math.min(...Object.values(playerTotals));

