    // Verify all holes have corresponding results
    const holeNumbersInLayout = new Set(layout.holes.map(h => h.holeNumber));
    const holeNumbersInResults = new Set(results.map(r => r.holeNumber));

    // Check if all layout holes are covered by results
    for (const holeNumber of holeNumbersInLayout) {
        if (!holeNumbersInResults.has(holeNumber)) {
            return 0;
        }
    }

    // Calculate total par for the course
    const totalPar = layout.holes.reduce((sum, hole) => sum + hole.par, 0);

    // Calculate total score from results
    const totalScore = results.reduce((sum, result) => sum + result.score, 0);

    // Check if total score is under par
